import React from "react";
import { variables } from "./Variables";
export class Student extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            students: [],
            modalTittle: "",
            studentid: 0,
            studentname: "",
            CONTACT: "",
            EMAIL: "",
            studentregno: "",
            Status: ""
        }
    }
    refreshList() {
        fetch(variables.API_URL + 'Student?Status=Active')
            .then(response => response.json())
            .then(data => {
                this.setState({ students: data });
            });
    }
    componentDidMount(){
        this.refreshList();
    }

    addclick(){
        this.setState({
            modalTittle: "Add student",
            studentid: 0,
            studentregno: "",
            studentname: "",
            EMAIL: "",
            CONTACT: "",
            Status: ""
        })
    }
    render(){
        const {
            students,
            modalTittle,
            studentid,
            studentname,
            CONTACT,
            EMAIL,
            studentregno,
            Status
        } = this.setState


        return(
            <div>
                <button type="button" className="btn btn-primary m-2 float-end" data-bs-toggle="modal"
                    data-bs-target="#exampleModal" onClick={()=>this.addclick()}>Add Student</button>
                <table className="table table-stripped">
                    <thead>
                        <th>Student Id</th>
                        <th>Registration Number</th>
                        <th>Student Name</th>
                        <th>Contact No</th>
                        <th>Email</th>
                        <th>Status</th>
                    </thead>
                    <tbody>
                        {
                            students?.map(s =>
                                <tr>
                                    <td>{s.studentid}</td>
                                    <td>{s.studentregno}</td>
                                    <td>{s.studentname}</td>
                                    <td>{s.CONTACT}</td>
                                    <td>{s.EMAIL}</td>
                                    <td>{s.Status}</td>
                                </tr>
                            )
                        }
                    </tbody>
                </table>
                <div className="modal fade" id="exampleMode" tabIndex="-1" aria-hidden="true">
                    <div className="modal-dialog modal-lg modal-dialog-centered">
                        <div className="modal-content">
                            <div className="modal-header">
                                <h5 className="modal-title">{modalTittle}</h5>
                                <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close">
                                </button>
                            </div>
                            <div className="modal-body">

                            </div>
                        </div>
                    </div>
                </div>
            </div>
        )
    }
}
