import React from "react";
import { variables } from "./Variables";
export class Room extends React.Component{
    constructor(props){
        super(props);
        this.setState = {
            rooms: [],
            modalTitle: "",
            room_id: 0,
            capacity: 0,
            room_no: ""
        }
    }

    refreshList(){
        fetch(variables.API_URL + 'Room')
        .then(response => response.json())
        .then(data => {
            this.setState({ rooms: data});
        });
    }

    componentDidCatch(){
        this.refreshList();
    }

    addClick(){
        this.setState({
            modalTitle: "Add Room",
            room_id: 0,
            capacity: 0,
            room_no: ""
        })
    }

    render(){
        const{
            rooms,
            modalTitle,
            room_id,
            capacity,
            room_no
        }=this.state

        return(
            <div>
                <button type="button" className="btn btn-primary m-2 float-end" data-bs-toggle="modal"
                data-bs-target="#exampleModal" data-bs-backdrop="static" onClick={()=>this.addClick()}>Add Room</button>
                <table className="table table-stripped">
                    <thead>
                        <tr>
                            <th>Room Id</th>
                            <th>Capacity</th>
                            <th>Room No</th>
                        </tr>
                    </thead>
                    {/* <tbody>
                        {
                            rooms?.map(r =>
                                <tr key={r.room_id}>
                                    <td>{r.room_id}</td>
                                    <td>{r.capacity}</td>
                                    <td>{r.room_no}</td>
                                </tr>
                            )
                        }
                    </tbody> */}
                </table>
            </div>
        )
    }

}
