import React from "react";
import { variables } from "./Variables";
export class Student extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            students : [],
            studentid: 0,
            studentname: "",
            CONTACT: "",
            EMAIL: "",
            studentregno: "",
            Status: ""
        }
    }
    refrehList(){
        fetch(variables.API_URL + "student")
        .then(response=>response.json())
        .then(data=>{this.setState({students:data});
        });
    }
    componentDidMount(){
        this.refrehList();
    }
    render(){
        const {
            students,
            studentid,
            studentname,
            CONTACT,
            EMAIL,
            studentregno,
            Status
        } = this.setState
        return(
            <div>
                <table className="table table-stripped">
                    <thead>
                        <th>Student Id</th>
                        <th>Registration Number</th>
                        <th>Student Name</th>
                        <th>Contact No</th>
                        <th>Email</th>
                        <th>Status</th>
                    </thead>
                    <tbody>
                        {
                            students?.map(s =>
                                <tr>
                                    <td>{s.studentid}</td>
                                    <td>{s.studentregno}</td>
                                    <td>{s.studentname}</td>
                                    <td>{s.CONTACT}</td>
                                    <td>{s.EMAIL}</td>
                                    <td>{s.Status}</td>
                                </tr>
                            )
                        }
                    </tbody>
                </table>
            </div>
        )
    }
}
